	def prova2(self):
		fc7.write("cnfg_ssa_scanchain_start_test",0)
		fc7.write("cnfg_ssa_scanchain_start_test",1)

	def prova1(self):
		self.fc7.write("cnfg_ssa_scanchain_is_capture_test",0)
		time.sleep(1)
		self.fc7.write("cnfg_ssa_scanchain_is_reset_test",0)
